mapper.select:
SELECT ss.id shopId, ss.shop_name shopName, od.id orderDetailId, od.order_id orderId, od.order_status orderStatus, case when od.order_status = 0 then '待付款' when od.order_status = 1 then '待发货' when od.order_status = 2 then '待收货' when od.order_status = 4 then '售后中' when od.order_status in(3, 5, -1) then '已完成' end as orderStatusName FROM s_order_detail od LEFT JOIN s_shop ss on ss.id = od.shop_id WHERE 1 = 1 AND od.user_id = #{userId}
AND od.order_status= #{status}
AND od.order_status in(3, 5, -1)
ORDER BY od.create_time DESC
