mapper.select:
SELECT o.id id, o.order_sn orderSn, o.user_id userId, o.task_id taskId, o.payment_type paymentType, o.payment_state paymentState, o.shipping_express_code shippingExpressCode, o.shipping_express_id shippingExpressId, o.shipping_code shippingCode, o.shipping_fee shippingFee, o.deliver_explain deliverExplain, o.address_id addressId, o.order_amount orderAmount, o.pay_amount payAmount, o.order_message orderMessage, o.order_status orderStatus, o.address_name addressName, o.address_phone addressPhone, o.address_detail addressDetail, o.product_id productId, o.product_number productNumber, o.shipping_time shippingTime, o.payment_time paymentTime, o.create_time createTime,
p.product_name productName, p.product_img productImg, p.product_des productDes, p.product_detail productDetail, p.product_price productPrice, p.price_unit priceUnit, e.e_name shippingExpressName
FROM s_order o LEFT JOIN s_product p ON p.id = o.product_id LEFT JOIN s_express e ON e.id = o.shipping_express_id
WHERE 1 = 1
AND o.user_id = #{sOrder.userId}
AND o.id = #{sOrder.id}
AND o.order_status = #{sOrder.orderStatus}
